mapper.select:
select userId, userPw
from member
where userId = #{userId} and userPw = #{userPw}

mapper.update:
update member
set userPw = #{userPw},
email = #{email},
nickname = #{nickname}
where userId = #{userId}

mapper.delete:
delete 
from member
where userId = #{userId} and userPw = #{userPw}

mapper.select:
select count(*) 
from member
where userId = #{userId}

select count(*)
from member
where nickname = #{nickname}

select count(*)
from member
where userId = #{userId} and userPw = #{userPw}

select email,nickname
from member
where userId = #{userId}
