mapper.select:
select * from planner
where userId = #{userId}
order by planno asc

mapper.update:
update planner
set planTitle = #{planTitle}, info =
#{info}
where userId = #{userId} and planNo = #{planNo}

mapper.delete:
delete planner
where userId = #{userId} and planNo =
#{planNo}

mapper.update:
update planner
set planImg = #{planImg}
where userId =
#{userId} and planNo = #{planNo}
